import java.util.Scanner;public class SUMITR{public static void main(String[] args){Scanner sc=new Scanner(System.in);int tt=sc.nextInt(); while(tt>0){int n=sc.nextInt();int max=Integer.MIN_VALUE;int[][] arr=new int[n+1][n+1];int[][] sum=new int[n+1][n+1];int counter=0;while(n>0){for(int i=0;i<counter+1;i++){arr[counter][i]=sc.nextInt();if(counter==0){sum[0][0]=arr[0][0];}else{if(counter>0 && i>0){sum[counter][i]=arr[counter][i]+Math.max(sum[counter-1][i],sum[counter-1][i-1]);}else if(counter>0 && i<1){sum[counter][i]=arr[counter][i]+sum[counter-1][i];}if(sum[counter][i]>max){max=sum[counter][i];}}}counter++;n--;}System.out.println(max);tt--;}}}
